                    <div id='textobc'>
                        <Alert color='dark' id='teste4'><span>{msg.texto}</span>
                        <br/><br/>
                            Digite:

                        </Alert>
                    </div>
                </div>
            </span>
        }

            </div>


    )
    } 


    if(msg.texto.substr(0,3) == "Ol√°"){
        return (

            <div key = {k}>

        {
            msg.origem === "user" && <span>



