import React, {Component} from 'react'
import {Alert} from 'reactstrap'
import {connect} from 'react-redux'
import '../chat/chat.css';



class ChatConversa extends Component{


   renderMensagem(msg,k){
    console.log(localStorage.getItem('contador'));
    if(msg.texto.substr(0,3) == "Por"){
        if(!localStorage.getItem('contador')){
            localStorage.setItem('contador', 1);
        }

        switch(localStorage.getItem('contador')){
            case '0': 
                    console.log('case 0',localStorage.getItem('contador'));
                    var textto = 'Seu CPF';
                    localStorage.setItem('contador', 1)
                    break;
            case '1': 
                    console.log('case 1',localStorage.getItem('contador'));
                    var textto  = 'Seu nome';

                    break;
            default:
                    console.log('default',localStorage.getItem('contador'));
                    var textto = 'Eu odeio minha vida';
                    break;
        }

        return (

            <div key = {k}>

        {
            msg.origem === "user" && <span>



            <div className='usuario'>

            <div id='textouc'>
                    <Alert color='info' id='teste'>{msg.texto}</Alert>
            </div>

                <div>
                    <img src='images/duvidas.png' id='userconversa'/>
                </div>

        </div>

        </span>

        }

        {
            msg.origem === "bot" && <span>
                <div className='iracema' id='fazendolink'>
                    <div>
                        <img src='images/iracema.jpg' id='botconversa'/>
                    </div>

                    <div id='textobc'>
                        <Alert color='dark' id='teste4'><span>{msg.texto}</span>
                        <br/><br/>
                        {textto}
                        </Alert>
                    </div>
                </div>
            </span>
        }

            </div>


    )
    } 


    if(msg.texto.substr(0,3) == "Ol√°"){
        return (

            <div key = {k}>

        {
            msg.origem === "user" && <span>



            <div className='usuario'>

            <div id='textouc'>
                    <Alert color='info' id='teste'>{msg.texto}</Alert>
            </div>

                <div>
                    <img src='images/duvidas.png' id='userconversa'/>
                </div>

        </div>

        </span>

        }

        {
            msg.origem === "bot" && <span>
                <div className='iracema' id='fazendolink'>
                    <div>
                        <img src='images/iracema.jpg' id='botconversa'/>
                    </div>

                    <div id='textobc'>
                        <Alert color='dark' id='teste4'><span>{msg.texto}</span>
                        <br/><br/>
                            Digite:<br/>
                            1 - Contatos<br/>
                            2 - Planos<br/>
                            3 - Solicitar representante<br/>
                            4 - Atendimento p√≥stumo
                        </Alert>
                    </div>
                </div>
            </span>
        }

            </div>


    )

    }

        if(msg.texto.substr(0, 5) == 'https'){
            return (

                <div key = {k}>

            {
                msg.origem === "user" && <span>

                {/* <Badge color='info' id="usertalk">Voc√™ disse</Badge> */}

                <div className='usuario'>

                <div id='textouc'>
                        <Alert color='info' id='teste'>{msg.texto}</Alert>
                </div>

                    <div>
                        <img src='images/duvidas.png' id='userconversa'/>
                    </div>

            </div>

            </span>

            }

            {
                msg.origem === "bot" && <span>
                    <div className='iracema' id='fazendolink'>
                        <div>
                            <img src='images/iracema.jpg' id='botconversa'/>
                        </div>

                        <a id='textobc' href={msg.texto}>
                            <Alert color='dark' id='teste3'>Clique aqui:<br/><span id='link'>{msg.texto}</span></Alert>
                        </a>
                    </div>
                </span>
            }

                </div>


        )

        }else{
            return (

                <div key = {k}>

            {
                msg.origem === "user" && <span>

                {/* <Badge color='info' id="usertalk">Voc√™ disse</Badge> */}

                <div className='usuario'>

                <div id='textouc'>
                        <Alert color='info' id='teste'>{msg.texto}</Alert>
                </div>

                    <div>
                        <img src='images/duvidas.png' id='userconversa'/>
                    </div>

            </div>

            </span>

            }

            {
                msg.origem === "bot" && <span>
                    <div className='iracema' id='fazendolink'>
                        <div>
                            <img src='images/iracema.jpg' id='botconversa'/>
                        </div>

                        <div id='textobc'>
                            <Alert color='dark' id='teste2'>{msg.texto}</Alert>
                        </div>
                    </div>
                </span>
            }

                </div>

            )
        }





}


    render(){

        return(
             <div className='chat-conversa'>
                {
                   Object.keys(this.props.mensagens).map(key => {
                        return this.renderMensagem(this.props.mensagens[key],key)
                    })
                }
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return{
        mensagens: state.chat.mensagens
    }
}

export default connect(mapStateToProps, null)(ChatConversa)
